DROP TABLE IF EXISTS `zd_client_session`;
CREATE TABLE `zd_client_session`  (
  `id` bigint(20) NOT NULL AUTO_INCREMENT,
  `client_key` varchar(4096) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '客户端唯一Key',
  `client_Mainversion` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '客户端主版本号',
  `client_Secondversion` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '客户端从版本号',
  `client_limitdata` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '客户端key有效时间',
  PRIMARY KEY (`id`) USING BTREE
) ENGINE = InnoDB CHARACTER SET = utf8 COLLATE = utf8_general_ci ROW_FORMAT = Dynamic;

ALTER TABLE zd_config ADD `ClientVerification` varchar(1) DEFAULT NULL COMMENT '是否启用客户端校验';
ALTER TABLE zd_config ADD `ClientVerificationType` varchar(1) DEFAULT NULL COMMENT '客户端校验方式，1:DES 2:RC4 3:RSA';
ALTER TABLE zd_config ADD `ClientVerificationData` varchar(9000) DEFAULT NULL COMMENT '客户端校验加密值';
ALTER TABLE zd_config ADD `ClientVerificationVersion` varchar(255) DEFAULT NULL COMMENT '客户端最低要求版本号(内部版本)';
ALTER TABLE zd_config ADD `Mobile_MainVersion` varchar(255) DEFAULT NULL COMMENT '移动端主版本号';
ALTER TABLE zd_config ADD `Mobile_SecondVersion` varchar(255) DEFAULT NULL COMMENT '移动端内部版本号';
ALTER TABLE zd_config ADD `Mobile_Downloadlink` text NULL COMMENT '移动端更新下载地址';
ALTER TABLE zd_config ADD `LicenseAgreement` text NULL COMMENT '许可协议';
ALTER TABLE zd_config ADD `PrivacyPolicy` text NULL COMMENT '隐私政策';
ALTER TABLE zd_config ADD `ipFirewall_Control` int(1) DEFAULT NULL COMMENT 'IP防火墙，0为禁用，1为启用';
ALTER TABLE zd_config ADD `ipFirewall_Mode` int(1) DEFAULT NULL COMMENT 'IP防火墙，0为白名单，1为黑名单，2为特定IP';
ALTER TABLE zd_download_record change downloadid softid varchar(255) DEFAULT NULL;
ALTER TABLE zd_type ADD `application` varchar(255) DEFAULT NULL;
ALTER TABLE zd_feedback ADD `status` varchar(255) DEFAULT NULL;

DROP TABLE IF EXISTS `zd_ipfirewall`;
CREATE TABLE `zd_ipfirewall`  (
  `id` bigint(20) NOT NULL AUTO_INCREMENT,
  `start_ip` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '起始IP',
  `end_ip` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '结束IP',
  `remark` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '备注',
  `ControlMode` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '绑定控制模式',
  PRIMARY KEY (`id`) USING BTREE
) ENGINE = InnoDB CHARACTER SET = utf8 COLLATE = utf8_general_ci ROW_FORMAT = Dynamic;


UPDATE zd_config SET `ClientVerification` = '';
UPDATE zd_config SET `ClientVerificationType` = '1';
UPDATE zd_config SET `ClientVerificationData` = 'source';
UPDATE zd_config SET `ClientVerificationVersion` = '(为空不生效)';
UPDATE zd_config SET `Mobile_MainVersion` = '1.0';
UPDATE zd_config SET `Mobile_SecondVersion` = '1';
UPDATE zd_config SET `Mobile_Downloadlink` = 'http://service.csource.com.cn/soft/main/Update.apk';
UPDATE zd_config SET `LicenseAgreement` = '更新日期：2024年04月02日\r\n生效日期：2024年04月02日\r\n\r\n1.前言\r\n\r\n重要提示，请仔细阅读：\r\n\r\n1.1本协议是您与Source签订的软件许可协议。使用Source软件之前，请您务必审慎阅读、充分理解协议中相关条款内容，尤其是以粗体或下划线标识的重要条款。您使用Source软件或点击\"同意\"键，即表示同意接受本协议的条款约束。如您不同意本协议的条款，请勿安装和/或使用Source软件，并且（如有提供选择\"同意\"或\"不同意\"条款）点击\"不同意\"键。\r\n \r\n1.2 同意使用数据\r\n\r\n您使用Source软件时，为实现账号注册、用户反馈等功能，Source可能需要收集您的个人信息（如手机号码、邮箱地址），对于前述可能收集的个人信息，Source会遵循合法处理个人信息的有关原则；运用合理的技术手段和管理制度保护您的个人信息的安全，保障您的合法权利。同时，Source也重视对未成年人个人信息的保护，如果您是未成年人，请在法定监护人的陪同下阅读本协议及其他相关协议，并特别注意未成年人相关条款。\r\n\r\n保护您的个人信息是Source的一项基本原则。Source制定了《隐私政策》，对个人信息收集、使用、共享、存储、删除等方面关系您切身利益的内容进行了重要披露。在任何时候，您的信息均依照Source的隐私政策处理。为更好地保护您的个人信息，在使用Source软件前，您应当已经完整阅读并充分了解《隐私政策》的内容。\r\n当您打开或使用这些功能时，视为您完全同意Source的条款。\r\n \r\n2.定义\r\n\r\n软件：本协议中的\"软件\"是指由若干模块或功能组成的信息处理程序或支持文件，其中支持文件具体包括软件的源代码、目标代码以及相关软件中所包含的图片、照片、图标、动画、录音、录像、音乐、文字、代码的全部或一部分，还包括与许可软件或Source产品相关的所有描述其功能、特点、内容、质量、测试、用户手册、软件许可协议等纸质或电子版的资料、技术文档等（以下统称\"软件产品\"或\"软件\"）。\r\n\r\n您：本协议中的\"您\"是指取得Source合法许可，具有使用本软件权利的个人或单个法人实体，法人实体包括公司、企业、机构、组织或单位。\r\n\r\n试用期：本协议中的试用期是指您在没有正式获得本软件的使用许可前，Source授权您为评估本软件目的而使用本软件的特定期间。\r\n \r\n3.软件许可\r\n\r\n在您遵守本协议内容的前提下，Source授予您的许可权利包括：\r\n\r\n1）试用期许可\r\n在试用期间，您可以为评估本软件目的而免费在您所控制的一台设备上安装本软件，并使用本软件提供的全部功能。\r\n\r\n2）商用许可\r\n\r\na）安装和使用权利：您可以在您所控制的一台设备上安装和使用本软件，使用本软件提供的功能。\r\n\r\nb）备份：您可以为存档目的复制一份单一备份副本。除非本协议另有规定，否则您不得以其它任何目的、其它任何方式复制软件的副本，包括软件随附的印刷材料、电子文档。\r\n \r\n4.许可使用方式和限制\r\n\r\n1）单一使用限制：您不得同时在一台以上的设备上进行安装使用本软件。但双方另有约定的除外。\r\n\r\n2）共享软件限制：您不得通过共享软件的全部或部分，在多台设备上使用软件的部分或全部功能。\r\n\r\n3）软件分解限制：您不得通过分解软件在不同的设备上使用软件的不同功能或把软件的不同部分嵌入到其他软件系统。\r\n\r\n4）软件完整性限制：您不得删除软件中的任何版权申明或提示，亦不得对软件中出现的任何商标或标识进行涂抹、修改或删除。\r\n\r\n5）反向工程、反编译、反汇编限制：您不得对软件进行反向工程、反编译、反汇编。\r\n\r\n6）遵守法律：您同意使用Source软件和/或服务须遵守所有适用的法律，包括在您居住或在阁下下载或者使用Source软件和/或服务的国家或地区的当地法律。Source软件的功能和服务未必以所有的语言或在所有地区提供。大华股份软件和服务某些功能需要连接互联网。\r\n\r\n7）保密限制：未经Source书面同意，您不得将本软件的性能或其他任何评估、测试结果、技术秘密或由本软件导出的任何该等保密信息透露给任何第三方。\r\n\r\n8）软件更新：Source软件会向Source定期查询有关更新,您可以在系统中查看最新版本信息并自主选择是否进行更新。不进行更新也不会影响您当前所使用的功能。\r\n \r\n5.知识产权声明\r\n\r\n1）本软件是由Source开发。Source保留对Source软件本身的著作权，以及保留一切未明确授予您的权利。\r\n\r\n2）本软件受中华人民共和国法律和国际条约保护。根据本协议，在此仅许可您非独占性的、非排他性的一般许可使用该软件的权利，而不是出售或转让。\r\n\r\n3）商标权：本协议不授予您Source或其供应商的任何商标或服务标志相关的任何权利。\r\n\r\n4）本软件所涉及到的一切知识产权，包括但不限于专利权、著作权、商标权、商业秘密、技术秘密，均属于各自内容拥有者的财产，Source保留从其所拥有的知识产权获取利益的权利。不包含在软件中，但可以通过使用软件访问的内容，其一切的所有权和知识产权也分属于各自内容的拥有者，并可能受适用版权或其它知识产权法和条约的保护。\r\n\r\n5）您进一步承认，由于您使用本软件而直接或间接产生的对该软件的任何改进、修改或调整所产生的或相关的知识产权均归Source所有。\r\n \r\n6.技术支持\r\n\r\n对于本软件在安装、使用过程中产生的任何疑问、问题，您可以及时与Source联系。\r\n  \r\n7.不作出担保\r\n\r\n7.1 若您将设备接入互联网需自担风险，包括但不限于可能遭受网络攻击、黑客攻击、病毒感染等，请您加强网络、设备数据和个人信息等的保护，采取保障设备网络安全的必要措施，包括但不限于修改默认密码并使用强密码、定期修改密码、将固件更新至最新版本等。Source不对因此造成的产品工作异常、信息泄露等问题承担任何责任。\r\n\r\n7.2 如您是消费者客户（指在您的行业、业务或专业以外使用Source软件的人），您在居住国可能享有法定权利，禁止以下限制适用于您。这些限制若被禁止即不适用于您。要进一步了解相关的权利，您应联系当地的消费者咨询组织。\r\n\r\n7.2.1 您明确承认并同意，在适用法律允许的范围内，您使用Source软件和通过Source软件执行或取用任何服务须自担风险，而有关质量满意度、性能、准确性及努力等方面的全部风险均由您承担。\r\n\r\n7.2.2 在适用法律允许的最大范围内，Source软件和服务都是\"按现状\"和\"可提供情况\"提供的，附有一切瑕疵而不带有任何种类的保证，Source特此否认就Source软件和服务作出任何明示、默示或法定保证和条件，包括但不限于有关适销性、质量满意度、适合作某特定用途、准确性、不受干扰地享用及不侵害第三方权利的默示保证和/或条件。\r\n\r\n7.2.3 Source并不担保您可不受干扰地享用Source软件和服务，Source软件包含的功能或所执行或提供的服务会符合您的要求，Source软件和服务将不受干扰地操作而且毫无错误、任何服务将持续提供、或Source软件或服务的瑕疵将被纠正，或Source软件将兼容或可与任何第三方软件、应用软件或第三方服务共同运作。\r\n\r\n7.2.4 Source授权代表给予的口头或书面的信息或意见均不构成任何担保。如发现Source软件或服务存有瑕疵，您须承担所有必要的维修、修理或纠正的全部费用。有些国家地区不允许排除默示担保或对消费者的适用法定权利加以限制的做法，因此上述排除和限制可能并不适用于您。\r\n \r\n8.责任限制\r\n\r\n在适用法律不禁止的范围内，在任何情况下，对由于您使用或无法使用大华股份软件和服务所引起或与此有关的任何人身伤害或任何附带的、特别的、间接的或后果性的损害赔偿，包括但不限于利润损失、数据混乱或损失、不能传播或接收任何数据、业务中断的损害赔偿或任何其它商业损害赔偿或损失，无论其成因及基于何种责任理论（合同、侵权或其它），Source及其关联公司、代理人或高层管理人员一概无须负责，即使Source已知悉可能发生上述损害赔偿。有些国家地区不允许对人身伤害或附带或后果性损害赔偿排除或附加责任限制，因此此项限制可能并不适用于您。在任何情况下Source就所有损害赔偿对您承担的全部责任（除在涉及人身伤害的情况中根据适用法律规定的损害赔偿外）不应超过您购买本软件时花费的款项。即使前述救济失去其基本作用，上述责任限制仍将适用。\r\n \r\n9.协议终止\r\n\r\n本协议维持有效，直至终止之时为止。如果您没有遵守本协议的部分或全部条款，本协议授予您的权利将会自动终止或以其他方式停止，而无需Source另行通知。且您知悉认可，您对本软件的使用有赖于Source及其关联公司为您提供的配套服务，您违反与Source或Source关联公司的条款、协议、规则、通告等相关规定，而被上述任一法人实体终止提供服务的，可能导致您无法正常使用本软件，我们有权终止使用许可。协议终止后，您必须立即停止使用该软件，删除已经复制、安装在设备上的软件所有内容及备份的副本；本协议第1.2、7、8、 11和12条在如此终止后继续有效。\r\n如果由于您违反了本协议的规定给Source或Source关联公司造成损失，您将或应该对该损失承担赔偿责任。\r\n \r\n10. 联系与投诉\r\n\r\n如果您对本协议有任何疑问、意见或建议，可通过以下方式与我们联系：\r\n\r\n1.    联系QQ：542112943  \r\n\r\n2.    联系邮箱：542112943@qq.com\r\n\r\n一般情况下，我们将尽快审核所涉问题，并在验证您的用户身份后15个工作日内予以回复。\r\n \r\n11.出口限制\r\n\r\n您应当遵守本软件所适用的国家出口和/或进口法律，以及依该国政府颁布的对最终用户、最终使用、目的地的限制。您不得在未先行获得必要的政府机关批准或许可的情况下，向任何依照该国法律限制出口的国家/地区直接或间接地出口或再出口本软件。该义务不因本协议终止而失效。\r\n \r\n12.管辖法律和可分割性\r\n\r\n本协议的解释、效力及纠纷的解决，均适用中华人民共和国法律。具有司法管辖权的法院裁定本协议的任何条款或其任何部分不可接受，本协议的其余规定应继续充分有效。任何因本协议产生的或与之相关的争议，双方同意提交Source所在地有管辖权的人民法院诉讼解决。\r\n \r\n13. 关于语言\r\n\r\n本协议内容包括协议正文、本协议明确援引的其他协议、Source已经发布的或将来可能发布的各类规则，该等内容均为本协议不可分割的组成部分，与协议正文具有同等法律效力。除另行明确声明外，您使用Source的软件均受本协议约束。未经Source签署书面文件, 对本协议的任何修订或修改不具约束力。本协议的任何译本仅仅为了满足当地需要。如果中文文本与任何非中文文本出现争议，在您所在国家地区当地法律不禁止的范围内，应以本协议的中文文本为准。\r\n \r\n14.其他\r\n\r\n我们可能会适时地对本协议进行修订或变更，当本协议的条款发生变更时，我们会在软件版本更新后您首次使用本软件时以弹窗的形式向您展示更新后的本协议。如果您不接受更新后的条款，请立即停止使用本软件。您继续使用本软件将被视为已接受了更新后的协议。\r\n';
UPDATE zd_config SET `PrivacyPolicy` = '最近更新日期：2024年04月02日\r\n生效日期：2024年04月02日\r\n\r\n本政策将帮助您了解以下内容：\r\n一、我们如何收集、使用您的个人信息\r\n二、我们如何共享、转让、公开披露您的个人信息\r\n三、我们如何保护和存储您的个人信息\r\n四、您的权利\r\n五、我们如何处理儿童的个人信息\r\n六、联系与投诉\r\n七、本政策如何更新\r\n\r\n\r\n    感谢您信任并使用本软件（以下又称\"软件\"）！保护用户的个人信息及隐私是我们的一项基本原则。您在使用本软件时，我们可能会收集和使用您的相关信息。我们希望通过本隐私政策（以下简称\"本政策\"）向您说明，我们如何收集、使用、储存、披露和处理这些信息，以及为保障您享有的相关权利我们为您提供的访问、更新、控制和保护这些信息的方式。\r\n\r\n    需特别说明的是，本政策不适用于由第三方公司或个人提供的软件或服务（以下简称\"第三方服务\"）。为方便您使用本软件，我们的软件内可能含有第三方服务或其链接；鉴于第三方将独立地收集并处理您的个人信息，我们无法控制，因此，除非另有法律规定，我们对第三方服务不承担责任，您需自行阅读理解第三方服务的隐私政策，并独立判断是否使用其服务。\r\n\r\n    由于本政策与您所使用的软件和/或服务息息相关，在您使用我们的软件和/或服务前，希望您仔细阅读并了解本政策，在需要时，按照本政策的指引，做出您认为适当的选择。本政策中涉及的相关技术词汇，我们尽量采用简明扼要的表述，以便您的理解。您使用或继续使用我们的软件和/或服务，即意味着同意我们按照本政策收集、使用、储存、披露和处理您的相关信息。\r\n\r\n    如果您有任何疑问、意见或建议，可以通过本政策第六节约定的方式与我们进行联系。\r\n\r\n\r\n一、我们如何收集、使用您的个人信息\r\n\r\n   （一）在您使用本软件的过程中，我们会按照下述条款收集相关信息，用以实现特定目的。\r\n\r\n      1.我们可能收集您的IP地址，目的是与本服务的服务器进行通信。\r\n\r\n      2.为实现问题反馈功能，您可能需要提供手机号码、电子邮箱以及您需要我们答复的问题或您向我们提出的意见，目的是对您反馈的问题或意见进行答复。拒绝提供这些信息仅会使您无法接收问题或意见反馈答复，但不影响您正常使用本服务的其他功能。\r\n\r\n      3.为实现本软件中的工具软件热度排名，我们会收集您的工具软件下载、使用记录，点击记录等日志信息，目的是为了分析工具软件的使用热度，优化工具软件功能和性能。请注意，单独的日志信息等是无法识别特定自然人身份的信息。如果我们将这类个人信息与其他信息结合用于识别特定自然人身份，或者将其与其他个人信息结合使用于其他目的的，则在结合使用前，我们会获得您授权同意或将该类个人信息做匿名化、去标识化处理。\r\n\r\n提醒您注意：\r\n\r\n      1、我们的软件和/或服务通过不同载体提供，且不同用户选择使用的具体软件/服务、实现的功能存在差异；相应地，具体软件收集使用的个人信息类型、范围等会有所区别，请以具体的软件/服务功能为准；\r\n\r\n      2、如果您向我们提供的信息中含有他人个人信息，须确保已合法取得相关主体的授权。\r\n\r\n   （二） 来自第三方的信息\r\n\r\n      本软件中的第三方服务由外部第三方主体提供，我们无法获得您在使用该类第三方服务时产生的信息。请您注意，目前我们不会主动从第三方获取您的个人信息。如未来为业务发展需要从第三方间接获取您个人信息，我们会在获取前向您明示您个人信息的来源、类型及使用范围，如我们开展业务需进行的个人信息处理活动超出您原本向第三方提供个人信息时的授权同意范围，我们将在处理您的该等个人信息前，征得您的明示同意；此外，我们也将会严格遵守相关法律法规的规定，并要求第三方保障其提供的信息的合法性。\r\n\r\n二、我们如何共享、转让、公开披露您的个人信息\r\n\r\n    我们不会主动共享或转让您的个人信息至第三方，如存在其他共享或转让您的个人信息情形时，我们会征得您的明示同意。\r\n\r\n    随着我们业务的持续发展，我们有可能进行合并、收购、资产转让等交易，我们将告知您相关情形，按照法律法规及不低于本政策所要求的标准继续保护或要求新的控制者继续保护您的个人信息。\r\n\r\n   （一）共享\r\n\r\n    我们不会与任何公司、组织和个人分享您的个人信息，但以下情况除外：\r\n\r\n     1.在获得您的明确同意后，我们会与其他方共享您的个人信息。\r\n\r\n     2.我们可能会根据法律法规规定，或按政府主管部门的强制性要求，共享您的个人信息。\r\n\r\n     3.与关联公司共享：为便于我们基于本软件向您提供软件和/或服务，推荐您可能感兴趣的信息，您的个人信息可能会与我们的关联公司共享。我们只会共享必要的个人信息，且受本政策中所声明目的的约束。关联公司如要改变个人信息的处理目的，将再次征求您的授权同意。\r\n\r\n    4.与合作伙伴共享：仅为实现本政策中声明的目的，我们的某些服务将由合作伙伴提供，我们可能会与合作伙伴共享您的某些个人信息，对我们与之共享个人信息的法律主体，我们会与其签署严格的保密协议和数据处理协议，要求他们按照我们的说明、本政策以及其他任何相关的保密和安全措施来处理个人信息。\r\n\r\n   （二）转让\r\n\r\n    我们不会将您的个人信息转让给任何公司、组织和个人，但以下情况除外：\r\n\r\n    1.在获取明确同意的情况下转让：获得您的明确同意后，我们会向其他方转让您的个人信息；\r\n\r\n    2.在涉及合并、收购或破产清算时，如涉及到个人信息转让，我们会要求新的持有您个人信息的公司、组织继续受本政策的约束，否则我们将要求该公司、组织重新向您征求授权同意。\r\n\r\n   （三）公开披露\r\n\r\n    我们仅会在以下情况下，公开披露您的个人信息：\r\n\r\n     1.获得您明确同意后；\r\n\r\n     2.基于法律的披露：在法律、法律程序、诉讼或政府主管部门强制性要求的情况下，我们可能会公开披露您的个人信息。\r\n\r\n     另外，根据相关法律法规及国家标准，以下情形中，我们可能会共享、转让、公开披露个人信息无需事先征得个人信息主体的授权同意：\r\n\r\n     1） 与国家安全、国防安全直接相关的；\r\n\r\n     2） 与公共安全、公共卫生、重大公共利益直接相关的；\r\n\r\n     3） 与犯罪侦查、起诉、审判和判决执行等直接相关的；\r\n\r\n     4） 出于维护个人信息主体或其他个人的生命、财产等重大合法权益但又很难得到本人同意的；\r\n\r\n     5） 个人信息主体自行向社会公众公开的个人信息；\r\n\r\n     6） 从合法公开披露的信息中收集个人信息的，如合法的新闻报道、政府信息公开等渠道。\r\n\r\n三、我们如何保护和存储您的个人信息\r\n\r\n   （一）个人信息的保护技术与措施\r\n\r\n    我们非常重视个人信息安全，并采取一切合理可行的措施，保护您的个人信息：\r\n\r\n    1. 我们的数据安全能力：我们会采用符合业界标准的安全防护措施，包括建立合理的制度规范、安全技术来防止您的个人信息遭到未经授权的访问使用、修改，避免数据的损坏或丢失。\r\n\r\n    2. 我们会采取一切合理可行的措施，确保未收集无关的个人信息。我们只会在达成本政策所述目的所需的期限内保留您的个人信息，除非需要延长保留期或受到法律的允许。\r\n\r\n    3. 我们通过不断提升的技术手段加强安装在您设备端的软件的安全能力，以防止您的个人信息泄露。\r\n\r\n    4. 互联网并非绝对安全的环境，而且电子邮件、即时通讯、及与其他用户的交流方式并未加密，我们强烈建议您不要通过此类方式发送个人信息。请使用复杂密码，协助我们保证您的账号安全。\r\n\r\n    5. 我们建立专门的管理制度、流程和组织以保障信息的安全。例如，我们严格限制访问信息的人员范围，要求他们遵守保密义务，并进行审计。\r\n\r\n    6. 若发生个人信息泄露等安全事件，我们将按照法律法规的要求，及时向您告知:安全事件的基本情况和可能的影响、我们已采取或将要采取的处置措施、您可自主防范和降低风险的建议、对您的补救措施等。我们会及时将事件相关情况以邮件、信函、电话、推送通知等方式告知您，难以逐一告知个人信息主体时，我们会采取合理、有效的方式发布公告。同时，我们还将按照监管部门要求，主动上报个人信息安全事件的处置情况。\r\n\r\n   （二）个人信息的存储\r\n\r\n     1、信息存储的地点\r\n\r\n     我们会按照法律法规规定，将境内收集的用户个人信息存储于中国境内。如果您的个人信息存储地点从中国境内转移到境外的，我们将严格依照法律的规定执行。\r\n\r\n     2、信息存储的期限\r\n\r\n     一般而言，我们仅为实现目的所必需的时间保留您的个人信息。\r\n\r\n     在您的个人信息超出保留期间后，我们会根据适用法律的要求删除您的个人信息，或使其匿名化处理。\r\n\r\n     如果我们终止服务或运营，我们会至少提前三十日向您通知，并在终止服务或运营后对您的个人信息进行删除或匿名化处理。\r\n\r\n四、您的权利\r\n\r\n    按照中国相关的法律、法规、标准，以及其他国家、地区的通行做法，您有访问、更正、删除我们收集的您的个人信息，和撤回对个人信息使用的同意的权利。您可以通过本政策的第六节中的联系方式与我们联系，进行个人信息的核实确认、更正、删除和撤回授权同意。\r\n\r\n    为保障安全，您可能需要提供书面请求，或以其他方式证明您的身份。我们可能会先要求您验证自己的身份，然后再处理您的请求。我们将在15个工作日内做出答复。\r\n\r\n    对于您合理的请求，我们原则上不收取费用，但对多次重复、超出合理限度的请求，我们将视情收取一定成本费用。对于那些无端重复、需要过多技术手段、给他人合法权益带来风险或者非常不切实际的请求，我们可能会予以拒绝。\r\n    \r\n    在以下情形中，按照法律法规要求，我们将无法响应您的请求：\r\n\r\n    1.国家安全、国防安全直接相关的；\r\n\r\n    2.公共安全、公共卫生、重大公共利益直接相关的；\r\n\r\n    3.犯罪侦查、起诉、审判和判决执行等直接相关的；\r\n\r\n    4.充分证据表明您存在主观恶意或滥用权利的；\r\n \r\n    5.应您的请求将导致您或其他个人、组织的合法权益受到严重损害的；\r\n\r\n    6.涉及商业秘密的。\r\n\r\n五、我们如何处理儿童的个人信息\r\n\r\n    我们的产品、网站和服务主要面向成人。根据相关法律法规的规定，若您是18周岁以下的未成年人，请务必在使用本服务前，在父母或其他监护人监护、指导下共同仔细阅读本隐私政策，并在征得监护人同意的前提下使用本服务或提供个人信息。如果父母或其他监护人有理由相信未成年人未经其事先同意而向我们提交了个人信息，请通过本政策第六节中的联系方式与我们联系。\r\n\r\n    如果我们发现自己在未事先获得可证实的父母或法定监护人同意的情况下收集了儿童的个人信息，我们会尽快删除相关数据。\r\n\r\n六、联系与投诉\r\n\r\n   （一）联系\r\n\r\n    如果您对本政策有任何疑问、意见或建议，可通过以下方式与我们联系：\r\n\r\n    1.    联系QQ：542112943 ； \r\n\r\n    2.    联系邮箱：542112943@qq.com \r\n\r\n    一般情况下，我们将尽快审核所涉问题，并在验证您的用户身份后15个工作日内予以回复。\r\n\r\n   （二）投诉\r\n\r\n    如您认为第三方侵犯您的合法权益，您应当按照我们的投诉规则或投诉程序向我们提交书面投诉，且您应当保证投诉书中的陈述和提供的相关材料皆是真实、有效和合法的。\r\n\r\n七、本政策如何更新\r\n\r\n    我们可能会适时地对本政策进行修订或变更，当本政策的条款发生变更时，我们会在软件版本更新后您首次使用本软件时以弹窗的形式向您展示变更后的政策。请您注意，只有在您点击弹窗中的同意按钮后，我们才会按照更新后的政策收集、使用、存储、处理您的个人信息。\r\n';
UPDATE zd_config SET `ipFirewall_Control` = '1';
UPDATE zd_config SET `ipFirewall_Mode` = '2';

UPDATE zd_type SET `application` = 'pc';